%include 'in_out.asm' ; подключение внешнего файлаSECTION .datamsg1: DB '5(x+18)-28',10, 'Введите значение x ',0rez: DB 'Результат: ',0SECTION .bssx: RESB 80SECTION .textGLOBAL _start_start:mov eax, msg1call sprintLFmov ecx, xmov edx, 80call sreadmov eax, xcall atoiadd eax, 18mov ebx, 5mul ebxadd eax, -28mov edi, eaxmov eax, rezcall sprintmov eax, edicall iprintLFcall quit